package com.silence.study.core.entity.album;import java.io.Serializable;import java.util.Date;import java.util.List;import com.origin.eurybia.jdbc.annotation.Column;import com.origin.eurybia.jdbc.annotation.ManyToOne;import com.origin.eurybia.jdbc.annotation.Validator;import com.silence.study.core.entity.company.BusCompanyEntity;/** * <br> * <b>功能：</b>公司相册表 Entity<br> * <b>作者：</b>@author Silence<br> * <b>日期：</b>2018-05-12 00:46:01<br> * <b>详细说明：</b>无<br> */public class BusAlbumEntity implements Serializable {		private static final long serialVersionUID = 1L;		/**	 * 相册id	 */	@Column(name="album_id")	private Integer albumId;	/**	 * 相册名称	 */	@Validator(name="相册名称",len = 50)	@Column(name="album_name")	private String albumName;    /**     * 缩略图     */    @Validator(name="缩略图",len = 50)    @Column(name="icon")    private String icon;	/**	 * 公司id	 */	@Column(name="company_id")	private Integer companyId;	/**	 * 相册描述	 */	@Validator(name="相册描述",len = 200)	@Column(name="album_desc")	private String albumDesc;    /**     * 相册描述     */    @Validator(name="相册描述",len = 11)    @Column(name="check_status")    private Integer checkStatus;	/**	 * 创建人	 */	@Validator(name="创建人",len = 50)	@Column(name="creator")	private String creator;	/**	 * 创建时间	 */	@Validator(name="创建时间",notNull = true)	@Column(name="create_time")	private Date createTime;    @ManyToOne(name = "b")    private BusCompanyEntity busCompanyEntity;	private List<BusAlbumDetailEntity> mediaDetailList;		public Integer getAlbumId() {	    return this.albumId;	}	public void setAlbumId(Integer albumId) {	    this.albumId=albumId;	}	public String getAlbumName() {	    return this.albumName;	}	public void setAlbumName(String albumName) {	    this.albumName=albumName;	}	public Integer getCompanyId() {	    return this.companyId;	}	public void setCompanyId(Integer companyId) {	    this.companyId=companyId;	}	public String getAlbumDesc() {	    return this.albumDesc;	}	public void setAlbumDesc(String albumDesc) {	    this.albumDesc=albumDesc;	}	public String getCreator() {	    return this.creator;	}	public void setCreator(String creator) {	    this.creator=creator;	}	public Date getCreateTime() {	    return this.createTime;	}	public void setCreateTime(Date createTime) {	    this.createTime=createTime;	}	public List<BusAlbumDetailEntity> getMediaDetailList() {		return mediaDetailList;	}	public void setMediaDetailList(List<BusAlbumDetailEntity> mediaDetailList) {		this.mediaDetailList = mediaDetailList;	}    public String getIcon() {        return icon;    }    public void setIcon(String icon) {        this.icon = icon;    }    public Integer getCheckStatus() {        return checkStatus;    }    public void setCheckStatus(Integer checkStatus) {        this.checkStatus = checkStatus;    }    public BusCompanyEntity getBusCompanyEntity() {        return busCompanyEntity;    }    public void setBusCompanyEntity(BusCompanyEntity busCompanyEntity) {        this.busCompanyEntity = busCompanyEntity;    }}